import React, { useState, useEffect } from 'react';
import axios from '../commons/axios';

const CatProduct = () => {
    const [cats, setCats] = useState([]); // Store cat data in state

    // Fetch cat data when the component mounts
    useEffect(() => {
        axios.get('/get-cats') // Adjust this URL to match your API
            .then(response => {
                setCats(response.data);
            })
            .catch(error => {
                // Handle the error
                console.error('There was an error!', error);
            });
    }, []);

    return (
        <div className="cat-product">
            <div className="container">
                <div className='columns'>
                    {cats.map((cat) => ( // Map over cat data to create a card for each
                        <div className="column" key={cat.id}>
                            <div className="card">
                                <div className="card-image">
                                    <figure className="image is-4by3">
                                        <img src={cat.image} alt={cat.name} />
                                    </figure>
                                </div>
                                <div className="card-content">
                                    <div className="media">
                                        <div className="media-content">
                                            <p className="title is-4">{cat.name}</p>
                                            <p className="subtitle is-6">{cat.breed}</p>
                                        </div>
                                    </div>
                                    <div className="content">
                                        {cat.description}
                                        <br />
                                        <span>Age: {cat.age}</span><br />
                                        <span>Location: {cat.location}</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default CatProduct;
