// import mongoose from 'mongoose';
import React, { useState, useEffect } from 'react';
import axios from '../commons/axios';
import SearchBar from './SearchBar';
import UseAuth from './UseAuth';
const mongoose = require('mongoose');
const CatProduct = () => {
    const [cats, setCats] = useState([]);
    const [search, setSearch] = useState('');
    const [cartNum, setCartNum] = useState(0);

    const { isLoggedIn, premission, userId } = UseAuth();

    useEffect(() => {
        axios.get('/get-cats')
            .then(response => {
                setCats(response.data);
            })
            .catch(error => {
                console.error('There was an error!', error);
            });


    }, []);

    const filteredCats = search
        ? cats.filter(cat =>
            cat.name.toLowerCase().includes(search.toLowerCase()) ||
            cat.breed.toLowerCase().includes(search.toLowerCase())
            // cat.description.toLowerCase().includes(search.toLowerCase())
        )
        : cats;


    // update cart 
    // update toolbox card nums 
    const updateCartNum = async (e) => {
        e.preventDefault();

        const cartNum = await initCarNum(userId)
        const objectId = mongoose.Types.ObjectId(userId);
        try {
            // Update the cartNum field in the user's employee object
            await axios.put(`/update-employees-accounts/${objectId}`, { cartNum });

            // Update the cartNum state
            setCartNum(cartNum);
        } catch (error) {
            console.error('Error while updating cartNum:', error);
        }

        setCartNum(cartNum)
    }


    const initCarNum = async (userId) => {
        try {
            const res = await axios.get(`/get-employees-accounts/${userId}`);
            const carts = res.data.employee.cartNum ? res.data.employee.cartNum + 1 : 1;
            return carts;
        } catch (error) {
            console.error('Error while fetching cart information:', error);
            return 0;
        }
    };



    return (
        <div className="cat-product">
            <div className="container">
                <div className='columns'>

                    <SearchBar cartNum={cartNum} search={search} setSearch={setSearch} />


                </div>


                <div className='columns is-multiline is-mobile'>
                    {filteredCats.map((cat) => (
                        <div className="column is-full-mobile is-half-tablet is-one-third-desktop is-one-quarter-widescreen" key={cat.id}>
                            <div className="card">
                                <button
                                    onClick={(e) => { updateCartNum(e) }}
                                    className="add-cart"
                                //  disabled={status === "unavailable"}
                                >
                                    <i className="fas fa-heart"></i>
                                    {/* <i className="fa-solid fa-heart" style="color: #ff0000;"></i> */}
                                </button>
                                <div className="card-image">
                                    <figure className="image is-4by3">
                                        <img src={cat.image} alt={cat.name} />
                                    </figure>
                                </div>
                                <div className="card-content">
                                    <div className="media">
                                        <div className="media-content">
                                            <p className="title is-4">{cat.name}</p>
                                            <p className="subtitle is-6">{cat.breed}</p>
                                        </div>
                                    </div>
                                    <div className="content">
                                        {cat.description}
                                        <br />
                                        <span>Age: {cat.age}</span><br />
                                        <span>Location: {cat.location}</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default CatProduct;
