import { useEffect, useState } from 'react';
import axios from '../commons/axios';
import { baseURL } from '../commons/helper';
import UseAuth from './UseAuth';

const ManageFavCartTable = () => {
    const [catData, setCatData] = useState([]);
    const [clickedCards, setClickedCards] = useState([]);

    const { isLoggedIn, premission, userId, objId } = UseAuth();

    useEffect(() => {
        fetchCatData();
    }, []);

    useEffect(() => {
        if (clickedCards.length > 0) {
            fetchClickedCatsData(userId);
        }
    }, [clickedCards]);

    const fetchCatData = async () => {
        try {
            const response = await axios.get(`${baseURL}/get-cats`);
            setCatData(response.data);
        } catch (error) {
            console.error('Error fetching cat data:', error);
        }
    };

    const fetchClickedCatsData = async (userId) => {
        try {
            const userId = userId;
            const response = await axios.get(`${baseURL}/get-employees-accounts/${userId}`);
            const clickedCardsData = response.data.employee.clickedCards;

            // Filter the cat data based on the clickedCards IDs
            const filteredCats = catData.filter(cat => clickedCardsData.includes(cat._id));

            // Update the cat data state
            setCatData(filteredCats);
        } catch (error) {
            console.error('Error fetching clicked cats data:', error);
        }
    };

    return (
        <div className="container">
            {/* Render the cat data */}
            {catData.map(cat => (
                <div key={cat._id}>
                    {/* Display cat details */}
                    <h3>{cat.name}</h3>
                    <p>Breed: {cat.breed}</p>
                    {/* Add more cat details as needed */}
                </div>
            ))}
        </div>
    );
};

export default ManageFavCartTable;
