import axios from '../commons/axios';
import React, { useState, useEffect } from 'react';
import { baseURL } from '../commons/helper';
import UseAuth from '../component/UseAuth';

const MessageList = () => {
    const [messages, setMessages] = useState([]);

    const { isLoggedIn, premission, userId, objId, location } = UseAuth();


    const fetchMessages = async () => {
        try {
            const response = await axios.get(`${baseURL}/get-messages`);
            const filteredMessages = response.data.data.filter(message => message.receiver === { location });
            setMessages(filteredMessages);
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };


    useEffect(() => {
        fetchMessages();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/delete-messages/${id}`);
            fetchMessages();
        } catch (error) {
            console.error('Error deleting message:', error);
        }
    };

    return (
        <div className="container">
            <table className="table is-bordered is-fullwidth">
                <thead>
                    <tr>
                        <th>Sender</th>
                        <th>Content</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {messages.map((message) => (
                        <tr key={message._id}>
                            <td>{message.sender}</td>
                            <td>{message.content}</td>
                            <td>
                                <button className="button is-danger" onClick={() => handleDelete(message._id)}>
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default MessageList;
