import axios from '../commons/axios';
import React, { useState, useEffect } from 'react';
import { baseURL } from '../commons/helper';
import UseAuth from '../component/UseAuth';

const MessageList = () => {
    const [messages, setMessages] = useState([]);

    const { isLoggedIn, premission, userId, objId, location } = UseAuth();

    useEffect(() => {
        if (location) {
            fetchMessages();
        }
    }, [location]);

    const fetchMessages = async () => {
        try {
            const response = await axios.get(`${baseURL}/get-messages`);
            const filteredMessages = response.data.data.filter(
                (message) => message.receiver === location
            );
            setMessages(filteredMessages);
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/delete-messages/${id}`);
            fetchMessages();
        } catch (error) {
            console.error('Error deleting message:', error);
        }
    };

    const formatDateTime = (dateTimeString) => {
        const options = {
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: 'numeric',
            minute: 'numeric',
            hour12: true,
            timeZone: 'Asia/Hong_Kong'
        };

        const dateTime = new Date(dateTimeString);
        return dateTime.toLocaleString('en-US', options);
    };

    return (
        <div className="container">
            <table className="table is-bordered is-fullwidth">
                <thead>
                    <tr>
                        <th>Sender</th>
                        <th>Content</th>
                        <th>Time</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {messages.map((message) => (
                        <tr key={message._id}>
                            <td>{message.sender}</td>
                            <td>{message.content}</td>
                            <td>{formatDateTime(message.createdAt)}</td>
                            <td>
                                <button
                                    className="button is-danger"
                                    onClick={() => handleDelete(message._id)}
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default MessageList;
