import React, { useState } from "react";
import axios from "../commons/axios";
import { baseURL } from "../commons/helper";


const TwoFactorAuthComponent = () => {
    const [email, setEmail] = useState("");
    const [tempCode, setTempCode] = useState("");

    const handleCodeVerification = () => {
        axios
            .post(`${baseURL}/api/auth/verify`, { email, tempCode })
            .then((response) => {
                // Handle successful response, e.g. navigate to the application
            })
            .catch((err) => {
                // Handle error, e.g. display an error message
            });
    };

    return (
        <div>
            <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <input
                type="text"
                placeholder="Temporary code"
                value={tempCode}
                onChange={(e) => setTempCode(e.target.value)}
            />
            <button onClick={handleCodeVerification}>Verify Code</button>
        </div>
    );
};

export default TwoFactorAuthComponent;
