import React, { useState } from 'react';
import axios from '../commons/axios';
import { baseURL } from '../commons/helper';
import { useHistory, useNavigate } from 'react-router-dom';

const LoginForm = () => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleLoginSuccess = (token, employeeID, position) => {
        localStorage.setItem('auth-token', token);
        localStorage.setItem('employeeID', employeeID);
        localStorage.setItem('position', position);
        // auto logout
        const logoutTime = Date.now() + 60 * 60 * 1000; // 60 minutes
        // const logoutTime = Date.now() + 1 * 5 * 1000; // 5 s for demo
        localStorage.setItem('logoutTime', logoutTime.toString());
        alert('Login successfully');
        navigate('/');
        window.location.reload();
    };


    const handleSubmit = async (e) => {
        e.preventDefault();

        // Perform client-side email and password format validation
        if (!validateEmail(email)) {
            alert('Please enter a valid email address.');
            return;
        }

        if (!validatePassword(password)) {
            alert('Please enter a valid password.');
            return;
        }

        try {
            const response = await axios.post(`${baseURL}/login`, {
                email: email,
                password: password,
            });

            if (response.data.message) {
                // First stage of login is complete, temporary code has been sent to the user's email
                alert(response.data.message);
                // Redirect or show UI to enter the temporary code
                // For example, you can navigate to the verification page
                navigate('/verify');
            } else if (response.data.token) {
                // Login is successful, handle success scenario
                const { token, employeeID, position } = response.data;
                localStorage.setItem('auth-token', token);
                localStorage.setItem('employeeID', employeeID);
                localStorage.setItem('position', position);
                // auto logout
                const logoutTime = Date.now() + 60 * 60 * 1000; // 60 minutes
                localStorage.setItem('logoutTime', logoutTime.toString());
                alert('Login successfully');
                navigate('/');
                window.location.reload();
            } else {
                alert('Invalid credentials. Please try again.');
            }
        } catch (error) {
            console.log('Error:', error);
            if (error.response && error.response.data) {
                alert(error.response.data.message);
            } else {
                alert('Incorrect Email or Password');
            }
        }
    };



    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    const validatePassword = (password) => {
        const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*]).{8,}$/;
        return passwordRegex.test(password);
    };

    return (
        <form className="login-form box" onSubmit={handleSubmit}>
            <div className="field">
                <p className="control has-icons-left">
                    <input
                        className="input"
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={handleEmailChange}
                        required
                    />
                    <span className="icon is-small is-left">
                        <i className="fas fa-envelope"></i>
                    </span>
                </p>
            </div>
            <div className="field">
                <p className="control has-icons-left">
                    <input
                        className="input"
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={handlePasswordChange}
                        required
                    />
                    <span className="icon is-small is-left">
                        <i className="fas fa-lock"></i>
                    </span>
                </p>
            </div>
            <div className="field">
                <p className="control">
                    <button type="submit" className="button is-success">
                        Login
                    </button>
                </p>
            </div>
        </form>
    );
};

export default LoginForm;
