import axios from '../commons/axios';
import React, { useState, useEffect } from 'react';
import { baseURL } from '../commons/helper';
import UseAuth from '../component/UseAuth';

const MessageList = () => {
    const [messageGroups, setMessageGroups] = useState([]);

    const { isLoggedIn, premission, userId, objId, location } = UseAuth();

    useEffect(() => {
        if (location) {
            fetchMessages();
        }
    }, [location]);

    const fetchMessages = async () => {
        try {
            const response = await axios.get(`${baseURL}/get-messages`);
            const messages = response.data.data;

            const groupedMessages = [];
            messages.forEach((message) => {
                const groupIndex = groupedMessages.findIndex(
                    (group) => group.sender === message.sender && group.receiver === message.receiver
                );

                if (groupIndex > -1) {
                    // Append to existing group
                    groupedMessages[groupIndex].content.push(message.content);
                    groupedMessages[groupIndex].createdAt.push(message.createdAt);
                    groupedMessages[groupIndex].updatedAt.push(message.updatedAt);
                } else {
                    // Create new group
                    groupedMessages.push({
                        sender: message.sender,
                        receiver: message.receiver,
                        content: [message.content],
                        createdAt: [message.createdAt],
                        updatedAt: [message.updatedAt]
                    });
                }
            });

            setMessageGroups(groupedMessages);
        } catch (error) {
            console.error('Error fetching messages:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/delete-messages/${id}`);
            fetchMessages();
        } catch (error) {
            console.error('Error deleting message:', error);
        }
    };

    return (
        <div className="container">
            {messageGroups.map((group) => (
                <div key={`${group.sender}-${group.receiver}`}>
                    <h2>Sender: {group.sender}</h2>
                    <h2>Receiver: {group.receiver}</h2>
                    <table className="table is-bordered is-fullwidth">
                        <thead>
                            <tr>
                                <th>Content</th>
                                <th>Time</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {group.content.map((content, index) => (
                                <tr key={index}>
                                    <td>{content}</td>
                                    <td>{group.createdAt[index]}</td>
                                    <td>
                                        <button
                                            className="button is-danger"
                                            onClick={() => handleDelete(group.updatedAt[index])}
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            ))}
        </div>
    );
};

export default MessageList;
