import HomePage from './page/HomePage'
import './app.scss'
import { BrowserRouter as Router, Route, Link, Routes, useNavigate } from 'react-router-dom';
import NotFound from './page/NotFound';
import ApplicationPage from './page/ApplicationPage';
import RegistrationPage from './page/RegistrationPage';
import LoginPage from './page/LoginPage';
import ApplicationListPage from './page/ApplicationListPage';
import ProtectedRoute from './component/ProtectedRoute';
import { useEffect } from 'react';

function App() {
  const navigate = useNavigate(); // Declare this variable


  const token = localStorage.getItem('auth-token');
  const position = localStorage.getItem('position');

  const logoutUser = () => {
    // Remove the auth token
    localStorage.removeItem('auth-token');
    // Remove the logout time
    localStorage.removeItem('logoutTime');
    // Optionally, you can also redirect the user to the login page
    navigate('/login');
  };


  useEffect(() => {
    const logoutTime = localStorage.getItem('logoutTime');
    if (logoutTime && Date.now() > logoutTime) {
      logoutUser();
    }
  }, []);


  return (
    // <div className='App'>
    //   <HomePage></HomePage>
    // </div>
    <Router>
      <div>

        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/application" element={<ApplicationPage />} />
          {!token && <Route path="/login" element={<LoginPage />} />}
          <Route path="/registration" element={<RegistrationPage />} />
          {token && position === 'System admin1' && <Route path="/application-list" element={<ProtectedRoute> <ApplicationListPage /> </ProtectedRoute>} />}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
